mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select d.*,c.name as categoryName
from dish d left join category c on d.category_id = c.id

    and d.name like concat('%',#{name},'%')
    and d.category_id = #{categoryId}
    and d.status = #{status}

order by d.update_time desc

select * from dish

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}
    and price = #{price}
